shader.vertex:
attribute vec3 Position;
attribute vec4 Color;
attribute vec2 TexCoord;
attribute vec4 RenderData;
attribute float Scale;
attribute float BloomAmount;
attribute vec2 Ratio;
varying vec4 Color0;
varying vec2 TexCoord0;
varying vec4 RenderDataOut;
varying float ScaleOut;
varying float BloomAmountOut;
varying vec2 RatioOut;
uniform mat4 Transform;
void main(void)
{
    RenderDataOut = RenderData;
    ScaleOut = Scale;           // Passing data to fragment shader
    BloomAmountOut = BloomAmount;   // Passing data to fragment shader
    Color0 = Color;
    TexCoord0 = TexCoord;
    gl_Position = Transform * vec4(Position.xyz, 1.0);
}

shader.fragment:
varying lowp vec4 Color0;
varying mediump vec2 TexCoord0;
varying lowp vec4 RenderDataOut;
varying lowp float ScaleOut;
varying mediump float BloomAmountOut;
uniform sampler2D Texture0;

void main(void)
{
    vec4 sum = vec4(0);
    vec2 texcoord = vec2(TexCoord0);
    gl_FragColor = Color0 * texture2D(Texture0, texcoord) + texture2D(Texture0, texcoord) * BloomAmountOut*0.8;       
}
